package JavaBasics;

public class LoopConcepts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		for (i = 10; i != 200;){
			System.out.println("The Value of i is "+ i );
			i = i +10;
		}
				
		int j = 0;
		while (j < 100){
			System.out.println("The value of J is " + j);
		j= j+5;
	}
	}
}
 
